mapper.select:
SELECT id, title, writer, publisher, pub_date, price, isbn10, isbn13, category, book_shelf, description, reg_date, edit_date, local_id_barcode, purchased_or_donated, available, additional_code, classification_code, author_code, volume_code, copy_code, tag, rf_id, book_or_not, page, book_size, image_link, library_id_lib, c.id_country, c.name_country FROM book_held h LEFT OUTER JOIN book_country c ON h.id_country= c.id_country WHERE library_id_lib=#{libraryIdLib} AND (available = 0 OR available = 1)
AND DATE_FORMAT(reg_date, '%Y') = #{regDate}
ORDER BY id ASC

SELECT id, title, writer, publisher, pub_date, price, isbn10, isbn13, category, book_shelf, description, reg_date, edit_date, local_id_barcode, purchased_or_donated, available, additional_code, classification_code, author_code, volume_code, copy_code, tag, rf_id, book_or_not, page, book_size, image_link, library_id_lib, c.id_country, c.name_country FROM book_held h
LEFT OUTER JOIN book_country c ON h.id_country= c.id_country WHERE library_id_lib=#{libraryIdLib} AND (local_id_barcode=#{localIdBarcode}
OR id=#{id}
)

SELECT
	min(copy_code+1)
FROM book_held
WHERE book_id_book=#{bookIdBook} AND library_id_lib = #{libraryIdLib}
	AND (available = 0 OR available = 1) AND (copy_code != 0)
	AND copy_code+1
	NOT IN (SELECT copy_code FROM book_held
						WHERE book_id_book=#{bookIdBook} AND library_id_lib = #{libraryIdLib}
						AND (available = 0 OR available = 1) AND (copy_code != 0))
LIMIT 1;

mapper.update:
UPDATE book_held SET
	edit_date=now(), local_id_barcode=CONCAT(#{localIdBarcode}, 'x'),
	available = 2
WHERE library_id_lib=#{libraryIdLib} AND local_id_barcode=#{localIdBarcode}
	AND id=#{id}

mapper.select:
SELECT id, title, additional_code, copy_code, classification_code, author_code, volume_code, local_id_barcode, name_lib FROM book_held h
INNER JOIN library l ON h.library_id_lib = l.id_lib WHERE library_id_lib=#{libraryIdLib} AND (available = 0 OR available = 1)
AND (
date_format(reg_date, '%Y-%m-%d') = #{regDate}
local_id_barcode = #{localIdBarcode}
(REPLACE(title,' ','')) LIKE concat('%', #{title}, '%') OR (title LIKE concat('%', #{title}, '%'))
)
ORDER BY sorting_index DESC
LIMIT #{limitStart}, #{listCount}

mapper.update:
UPDATE book_held SET
 	sorting_index=#{sortingIndex}
 WHERE library_id_lib=#{libraryIdLib} AND available=1
 		AND id=#{id}

UPDATE book_held SET
 	tag=#{tag}
 WHERE library_id_lib=#{libraryIdLib}
 		AND id=#{id}
